import './App.css';
import { useEffect, useState } from 'react';
import axios from 'axios'
import { Card } from './components/Card';
import { Input } from './components/UI/Input/Input';
import { Button } from './components/UI/button/Buttons';
function App() {

  const [myLists, setMyList] = useState([
    {
      userId: 1,
      id: Date.now,
      title: 'Многие думают, что Lorem Ipsum - взятый с потолка псевдо-латинский набор слов, но это не совсем так.',
      completed: false
    },
    {
      userId: 1,
      id: Date.now,
      title: 'Давно выяснено, что при оценке дизайна и композиции читаемый текст мешает сосредоточиться. ',
      completed: false
    },
    {
      userId: 1,
      id: Date.now,
      title: 'Есть много вариантов Lorem Ipsum,',
      completed: false
    },
  ])

  const [title, setTitle] = useState('')

  const [list, setList] = useState([])

  function addNewCard(e) {
    const newCard = {
      userId: 1,
      id: Date.now,
      title: title,
      completed: false
    }
    setMyList([...myLists, newCard])
    setTitle('')
  }
  const handleDelete = (id) => {
    const updatedTodos = myLists.filter(myList => myList.id !== id);
    setTodos(updatedTodos);
  };

  useEffect(() => {
    const show = async () => {
      try {
        const response = await axios.get('https://jsonplaceholder.typicode.com/todos')
        setList(response.data)
      } catch (error) {
        console.error('Something wrong', error);
      }
    }
    show()
  }, [])



  return (
    <div className="App">
      <Input value={title} setValue={setTitle} />
      <div className='Buttons'>
        <Button onClick={addNewCard}>Add</Button>
        <Button onClick={handleDelete}>Delete</Button>
      </div>
      {myLists.map(myList => <Card key={myList.id}>{myList.title}</Card>)}
      {list.map(item => <Card key={item.id}>{item.title}</Card>)}

    </div>
  );
}

export default App;
